'use strict';

exports.name = 'CagGroup';

exports.setUp = (app, mongoose) =>
{
  const cagGroupSchema = new mongoose.Schema({
    name: {
      type: String,
      required: true,
      trim: true
    },
    color: {
      type: String,
      default: '#FFFFFF'
    },
    cags: [String]
  }, {
    id: false
  });

  cagGroupSchema.statics.TOPIC_PREFIX = 'cagGroups';
  cagGroupSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, cagGroupSchema);
};
