'use strict';

exports.name = 'Company';

exports.setUp = (app, mongoose) =>
{
  const companySchema = new mongoose.Schema({
    _id: {
      type: String,
      required: true,
      trim: true
    },
    name: {
      type: String,
      trim: true
    },
    shortName: {
      type: String,
      trim: true
    },
    color: {
      type: String,
      default: '#000000'
    },
    syncPatterns: [String]
  }, {
    id: false
  });

  companySchema.statics.TOPIC_PREFIX = 'companies';
  companySchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, companySchema);
};
