'use strict';

exports.name = 'DelayReason';

exports.setUp = (app, mongoose) =>
{
  const notificationSchema = new mongoose.Schema({
    subdivisions: [{
      type: mongoose.Schema.Types.ObjectId,
      ref: 'Subdivision'
    }],
    prodFunctions: [{
      type: String,
      ref: 'ProdFunction'
    }]
  }, {
    _id: false,
    minimize: false
  });

  const delayReasonSchema = new mongoose.Schema({
    active: {
      type: Boolean,
      required: true
    },
    name: {
      type: String,
      trim: true,
      required: true
    },
    drm: String,
    notifications: [notificationSchema]
  }, {
    id: false
  });

  delayReasonSchema.statics.TOPIC_PREFIX = 'delayReasons';
  delayReasonSchema.statics.PRIVILEGES_VIEW = ['LOCAL', 'USER'];
  delayReasonSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, delayReasonSchema);
};
