'use strict';

exports.name = 'Division';

exports.setUp = (app, mongoose) =>
{
  const divisionSchema = new mongoose.Schema({
    _id: {
      type: String,
      required: true,
      trim: true
    },
    type: {
      type: String,
      enum: ['prod', 'dist', 'other'],
      default: 'prod'
    },
    description: {
      type: String,
      trim: true
    },
    deactivatedAt: {
      type: Date,
      default: null
    }
  }, {
    id: false
  });

  divisionSchema.statics.TOPIC_PREFIX = 'divisions';
  divisionSchema.statics.BROWSE_LIMIT = 1000;

  divisionSchema.pre('remove', function(next)
  {
    mongoose.model('Subdivision').findOne({division: this._id}).select({_id: 1}).lean().exec((err, child) =>
    {
      if (err)
      {
        return next(err);
      }

      if (child)
      {
        return next(app.createError(`Org unit not empty: ${child._id}`, 'NOT_EMPTY', 400));
      }

      next();
    });
  });

  mongoose.model(exports.name, divisionSchema);
};
