'use strict';

module.exports = (app, module) =>
{
  const {express, user, Event} = module;

  const canView = user.auth('EVENTS:VIEW');

  express.get('/events', canView, express.crud.browseRoute.bind(null, app, Event));

  express.get('/events/types', canView, getTypesRoute);

  express.get('/events/pending', canView, getPendingRoute);

  express.post('/events/pending', user.auth('SUPER'), insertPendingRoute);

  /**
   * @private
   * @param {Object} req
   * @param {Object} res
   */
  function getTypesRoute(req, res)
  {
    res.send(Object.keys(module.types).sort());
  }

  /**
   * @private
   * @param {Object} req
   * @param {Object} res
   */
  function getPendingRoute(req, res)
  {
    res.send(module.getPendingEvents());
  }

  /**
   * @private
   * @param {Object} req
   * @param {Object} res
   */
  function insertPendingRoute(req, res)
  {
    const beforeCount = module.getPendingEvents().length;

    module.insertEvents();

    const afterCount = module.getPendingEvents().length;

    res.send({
      beforeCount,
      afterCount
    });
  }
};
