'use strict';

exports.name = 'CurrencyRate';

exports.setUp = (app, mongoose) =>
{
  const currencyRateSchema = new mongoose.Schema({
    _id: Date,
    rates: {}
  }, {
    id: false
  });

  currencyRateSchema.statics.TOPIC_PREFIX = 'currencyRates';
  currencyRateSchema.statics.BROWSE_LIMIT = 365;

  mongoose.model(exports.name, currencyRateSchema);
};
