'use strict';

module.exports = async (app, module, req, res, next) =>
{
  try
  {
    await module.updateCurrency(req.body.currency, req.body.from, req.body.to);

    res.sendStatus(204);
  }
  catch (err)
  {
    next(err);
  }
};
