'use strict';

exports.name = 'IsaPalletKind';

exports.setUp = (app, mongoose) =>
{
  const isaPalletKind = new mongoose.Schema({
    shortName: {
      type: String,
      required: true,
      trim: true
    },
    fullName: {
      type: String,
      required: false,
      trim: true
    }
  }, {
    id: false
  });

  isaPalletKind.statics.TOPIC_PREFIX = 'isaPalletKinds';
  isaPalletKind.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, isaPalletKind);
};
