'use strict';

exports.DEFAULT_CONFIG = {
  mongooseId: 'mongoose',
  expressId: 'express',
  sioId: 'sio',
  userId: 'user',
  mailSenderId: 'mail/sender',
  reportsId: 'reports',
  attachmentsDest: null,
  emailUrlPrefix: 'http://127.0.0.1/',
  multiType: true,
  remind: 14
};

exports.models = [
  require('./models/kaizenArea'),
  require('./models/kaizenBehaviour'),
  require('./models/kaizenCategory'),
  require('./models/kaizenCause'),
  require('./models/kaizenOrder'),
  require('./models/kaizenProductFamily'),
  require('./models/kaizenRisk'),
  require('./models/kaizenSection'),
  require('user/models/user')
];

exports.optionalModules = {
  'mongoose': [
    require('./durationRecalculator'),
    require('./stats'),
    require('./observerCleaner')
  ],
  'mongoose mailSender': [
    require('./notifier'),
    require('./reminder')
  ],
  'mongoose sio user': require('./commands'),
  'mongoose user reports express': require('./routes')
};

exports.start = (app, module) =>
{
  if (module.config.emailUrlPrefix.substr(-1) !== '/')
  {
    module.config.emailUrlPrefix += '/';
  }

  module.DICTIONARIES = {
    sections: 'KaizenSection',
    areas: 'KaizenArea',
    categories: 'KaizenCategory',
    causes: 'KaizenCause',
    risks: 'KaizenRisk',
    productFamilies: 'KaizenProductFamily',
    behaviours: 'KaizenBehaviour'
  };
};
