'use strict';

exports.name = 'KaizenBehaviour';

exports.setUp = (app, mongoose) =>
{
  const langSchema = new mongoose.Schema({
    name: {
      type: String,
      required: true,
      trim: true
    },
    description: {
      type: String,
      trim: true,
      default: ''
    }
  }, {
    _id: false
  });

  const kaizenBehaviourSchema = new mongoose.Schema({
    _id: {
      type: String,
      required: true,
      match: /^[A-Za-z0-9-]+$/
    },
    position: {
      type: Number,
      default: 0
    },
    lang: {
      pl: langSchema,
      en: langSchema
    }
  }, {
    id: false
  });

  kaizenBehaviourSchema.statics.TOPIC_PREFIX = 'kaizen.behaviours';
  kaizenBehaviourSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, kaizenBehaviourSchema);
};
