'use strict';

exports.name = 'KaizenControlList';

exports.setUp = (app, mongoose) =>
{
  const categorySchema = new mongoose.Schema({
    _id: {
      type: mongoose.Schema.Types.ObjectId,
      ref: 'KaizenControlCategory'
    },
    shortName: {
      type: String,
      required: true
    },
    fullName: {
      type: String,
      required: true
    }
  }, {
    _id: false
  });

  const schema = new mongoose.Schema({
    active: {
      type: Boolean,
      default: true
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    categories: [categorySchema]
  }, {
    id: false
  });

  schema.statics.TOPIC_PREFIX = 'kaizen.controlLists';
  schema.statics.BROWSE_LIMIT = 1000;

  return schema;
};
