'use strict';

exports.name = 'KaizenProductFamily';

exports.setUp = (app, mongoose) =>
{
  const kaizenProductFamilySchema = new mongoose.Schema({
    _id: {
      type: String,
      required: true,
      match: /^[A-Za-z0-9-]+$/
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    position: {
      type: Number,
      default: 0
    },
    active: {
      type: Boolean,
      default: true
    },
    owners: [{}],
    mrps: [String]
  }, {
    id: false
  });

  kaizenProductFamilySchema.statics.TOPIC_PREFIX = 'kaizen.productFamilies';
  kaizenProductFamilySchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, kaizenProductFamilySchema);
};
