'use strict';

exports.name = 'KaizenSection';

exports.setUp = (app, mongoose) =>
{
  const kaizenSectionSchema = new mongoose.Schema({
    _id: {
      type: String,
      required: true,
      match: /^[A-Za-z0-9-]+$/
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    position: {
      type: Number,
      default: 0
    },
    subdivisions: [mongoose.Schema.Types.ObjectId]
  }, {
    id: false
  });

  kaizenSectionSchema.statics.TOPIC_PREFIX = 'kaizen.sections';
  kaizenSectionSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, kaizenSectionSchema);
};
