'use strict';

exports.name = 'KaizenSection';

exports.setUp = (app, mongoose) =>
{
  const sectionSchema = new mongoose.Schema({
    _id: {
      type: String,
      required: true,
      match: /^[A-Za-z0-9-]+$/
    },
    active: {
      type: Boolean,
      default: true
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    position: {
      type: Number,
      default: 0
    },
    subdivisions: [mongoose.Schema.Types.ObjectId],
    confirmers: [{id: String, label: String}],
    coordinators: [{id: String, label: String}]
  }, {
    id: false
  });

  sectionSchema.statics.TOPIC_PREFIX = 'kaizen.sections';
  sectionSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, sectionSchema);
};
