'use strict';

exports.name = 'KaizenTopic';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    active: {
      type: Boolean,
      default: true
    },
    position: {
      type: Number,
      default: 0
    },
    shortName: {
      type: String,
      required: true,
      trim: true
    },
    fullName: {
      type: String,
      required: true,
      trim: true
    }
  }, {
    id: false
  });

  schema.statics.TOPIC_PREFIX = 'kaizen.topics';
  schema.statics.BROWSE_LIMIT = 1000;

  return schema;
};
