'use strict';

exports.name = 'KanbanContainer';

exports.setUp = (app, mongoose) =>
{
  const kanbanContainerSchema = new mongoose.Schema({
    _id: String,
    name: String,
    length: Number,
    width: Number,
    height: Number,
    image: String
  }, {
    id: false,
    minimize: false
  });

  kanbanContainerSchema.statics.TOPIC_PREFIX = 'kanban.containers';

  mongoose.model(exports.name, kanbanContainerSchema);
};
