'use strict';

const generateLicenseKey = require('./generateLicenseKey');

exports.DEFAULT_CONFIG = {
  mongooseId: 'mongoose',
  userId: 'user',
  expressId: 'express',
  licenseEd: {
    pem: null,
    password: null
  },
  defaultLicensee: ''
};

exports.models = [
  require('./models/license'),
  require('./models/licensePing')
];

exports.optionalModules = {
  'mongoose user express': require('./routes')
};

exports.start = (app, module) =>
{
  const config = module.config;

  module.licenseEdKey = null;

  if (config.licenseEd.pem)
  {
    if (config.licenseEd.password)
    {
      module.licenseEdKey = {
        key: config.licenseEd.pem,
        passphrase: config.licenseEd.password
      };
    }
    else
    {
      module.licenseEdKey = {
        key: config.licenseEd.pem
      };
    }
  }

  module.generateLicenseKey = generateLicenseKey.bind(null, module.licenseEdKey);
};
