'use strict';

exports.name = 'LicensePing';

exports.setUp = (app, mongoose) =>
{
  const licensePingSchema = new mongoose.Schema({
    uuid: {
      type: String,
      ref: 'License',
      required: true
    },
    pingedAt: {
      type: Date,
      required: true
    },
    ip: {
      type: String,
      required: true
    },
    granted: {
      type: Boolean,
      required: true
    },
    meta: {}
  }, {
    id: false
  });

  licensePingSchema.statics.TOPIC_PREFIX = 'licensePings';

  licensePingSchema.index({uuid: 1, pingedAt: -1});

  mongoose.model(exports.name, licensePingSchema);
};
