'use strict';

exports.name = 'BrowserError';

exports.setUp = (app, mongoose) =>
{
  const browserError = new mongoose.Schema({
    time: Date,
    user: {},
    headers: {},
    versions: {},
    browser: {},
    error: {}
  }, {
    id: false,
    versionKey: false
  });

  browserError.statics.TOPIC_PREFIX = 'logs.browserErrors';
  browserError.statics.BROWSE_LIMIT = 100;

  browserError.index({time: -1});

  mongoose.model(exports.name, browserError);
};
