'use strict';

exports.name = 'LossReason';

exports.setUp = (app, mongoose) =>
{
  const lossReasonSchema = new mongoose.Schema({
    _id: {
      type: String,
      required: true,
      trim: true
    },
    label: {
      type: String,
      trim: true
    },
    position: {
      type: Number,
      default: 0
    }
  }, {
    id: false
  });

  lossReasonSchema.statics.TOPIC_PREFIX = 'lossReasons';
  lossReasonSchema.statics.PRIVILEGES_VIEW = ['DICTIONARIES:VIEW', 'PRESS_WORKSHEETS:MANAGE'];
  lossReasonSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, lossReasonSchema);
};
