'use strict';

const step = require('h5.step');

module.exports = (app, module, orderNo, operationNo, done) =>
{
  const {
    ComponentLabel,
    Order
  } = module;

  step(
    function()
    {
      if (!orderNo || !operationNo)
      {
        return this.skip(null, []);
      }

      Order
        .findById(orderNo)
        .select({operations: 1, bom: 1})
        .lean()
        .exec(this.next());
    },
    function(err, sapOrder)
    {
      if (err)
      {
        return this.skip(err);
      }

      if (!sapOrder)
      {
        return this.skip(null, []);
      }

      const operation = (sapOrder.operations || []).find(op => op.no === operationNo);

      if (!operation)
      {
        return this.skip(null, []);
      }

      const componentCodes = [];

      (sapOrder.bom || []).forEach(component =>
      {
        if (component.nc12)
        {
          componentCodes.push(component.nc12);
        }
      });

      ComponentLabel
        .find({
          operationNo: operation.no,
          componentCode: {$in: componentCodes}
        })
        .lean()
        .exec(this.next());
    },
    done
  );
};
