'use strict';

exports.name = 'ComponentLabelCounter';

exports.setUp = (app, mongoose) =>
{
  const componentLabelSchema = new mongoose.Schema({
    orderNo: String,
    componentCode: String,
    counter: Number
  }, {
    id: false,
    versionKey: false
  });

  componentLabelSchema.statics.TOPIC_PREFIX = 'componentLabels.counters';
  componentLabelSchema.statics.BROWSE_LIMIT = 100;

  componentLabelSchema.index({orderNo: 1, componentCode: 1});
  componentLabelSchema.index({componentCode: 1});

  mongoose.model(exports.name, componentLabelSchema);
};
