'use strict';

exports.name = 'OrderDocumentChange';

exports.setUp = (app, mongoose) =>
{
  const changeSchema = new mongoose.Schema({
    user: {},
    time: Date,
    nc15: String,
    type: String,
    data: {}
  }, {
    id: false
  });

  changeSchema.statics.BROWSE_LIMIT = 100;

  changeSchema.index({nc15: 1});

  mongoose.model(exports.name, changeSchema);
};
