'use strict';

exports.name = 'OrderDocumentConfirmation';

exports.setUp = (app, mongoose) =>
{
  const confirmationSchema = new mongoose.Schema({
    user: {},
    time: {
      type: Date,
      required: true
    },
    nc15: {
      type: String,
      required: true
    },
    name: String,
    line: {
      type: String,
      required: true
    },
    station: {
      type: Number,
      required: true,
      min: 1
    },
    orderNo: {
      type: String,
      required: true
    }
  }, {
    id: false,
    versionKey: false
  });

  confirmationSchema.statics.BROWSE_LIMIT = 100;

  confirmationSchema.index({time: -1});
  confirmationSchema.index({nc15: 1, time: -1});
  confirmationSchema.index({line: 1, time: -1});

  mongoose.model(exports.name, confirmationSchema);
};
