'use strict';

exports.name = 'OrderDocumentFolder';

exports.setUp = (app, mongoose) =>
{
  const orderDocumentFolderSchema = new mongoose.Schema({
    _id: String,
    name: String,
    parent: String,
    children: [String],
    oldParent: {
      type: String,
      default: null
    },
    funcs: [String]
  }, {
    id: false
  });

  orderDocumentFolderSchema.statics.BROWSE_LIMIT = 2000;

  orderDocumentFolderSchema.index({parent: 1});

  mongoose.model(exports.name, orderDocumentFolderSchema);
};
