'use strict';

exports.name = 'OrderDocumentStatus';

exports.setUp = (app, mongoose) =>
{
  const orderDocumentStatusModel = new mongoose.Schema({
    _id: {
      type: String,
      required: true
    },
    statusDate: {
      type: Number,
      required: true
    },
    files: [String]
  }, {
    id: false
  });

  mongoose.model(exports.name, orderDocumentStatusModel);
};
