'use strict';

module.exports = (app, {updater}, req, res) =>
{
  res.format({
    'text/html': function()
    {
      res.render('index', updater.getAppTemplateData('docs', req, {
        CLIENT_ID: JSON.stringify(req.params.clientId || req.query.COMPUTERNAME || null)
      }));
    }
  });
};
