'use strict';

exports.name = 'ChangeNumber';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    _id: String,
    authGroup: String,
    validFrom: Date,
    createdOn: Date,
    createdBy: String,
    changedOn: Date,
    changedBy: String,
    description: String,
    reason: String
  }, {
    id: false,
    minimize: false
  });

  schema.statics.TOPIC_PREFIX = 'orders.changeNumbers';

  return schema;
};
