'use strict';

exports.name = 'Component';

exports.setUp = (app, mongoose) =>
{
  const componentSchema = new mongoose.Schema({
    _id: String,
    name: String
  }, {
    id: false
  });

  componentSchema.statics.TOPIC_PREFIX = 'components';

  mongoose.model(exports.name, componentSchema);
};
