'use strict';

exports.name = 'OrderEtoContinuation';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    _id: String,
    firstAt: Date,
    continuedAt: Date
  }, {
    id: false
  });

  schema.statics.TOPIC_PREFIX = 'orders.etoContinuations';
  schema.statics.BROWSE_LIMIT = 100;

  mongoose.model(exports.name, schema);
};
