'use strict';

exports.name = 'OrderIntake';

exports.setUp = (app, mongoose) =>
{
  const orderIntakeSchema = new mongoose.Schema({
    _id: {
      no: String,
      item: String
    },
    nc12: String,
    description: String,
    qty: Number,
    soldToParty: String,
    shipTo: String,
    salesOrderDate: Date
  }, {
    id: false
  });

  mongoose.model(exports.name, orderIntakeSchema);
};
