'use strict';

exports.name = 'OrderZlf1';

exports.setUp = (app, mongoose) =>
{
  const orderZlf1Schema = new mongoose.Schema({
    _id: String,
    ts: Date
  }, {
    id: false,
    minimize: false,
    strict: false
  });

  orderZlf1Schema.index({ts: -1});

  orderZlf1Schema.statics.prepareForInsert = function(data, ts)
  {
    const orderZlf1 = {ts};

    Object.keys(data).forEach(function(key)
    {
      let value = data[key].trim();

      key = key.toLowerCase().replace(/[^a-z0-9_]+/g, ' ').trim().replace(/(\s|_)+/g, '_');

      if (!key.length)
      {
        return;
      }

      if (key === 'production_order_nr')
      {
        key = '_id';
        value = value.replace(/^0+/, '');
      }

      orderZlf1[key] = value;
    });

    return orderZlf1;
  };

  mongoose.model(exports.name, orderZlf1Schema);
};
