'use strict';

exports.name = 'ProductMessage';

exports.setUp = (app, mongoose) =>
{
  const productMessageSchema = new mongoose.Schema({
    nc12: [String],
    text: String
  }, {
    id: false
  });

  productMessageSchema.index({nc12: 1});

  productMessageSchema.statics.TOPIC_PREFIX = 'orders.productMessages';

  mongoose.model(exports.name, productMessageSchema);
};
