'use strict';

module.exports = (app, {express, user, EmptyOrder}) =>
{
  const canView = user.auth('ORDERS:VIEW');

  express.get('/emptyOrders', canView, express.crud.browseRoute.bind(null, app, EmptyOrder));
};
