'use strict';

module.exports = (app, module) =>
{
  const {
    express,
    user,
    ProductNote
  } = module;

  const canView = user.auth('ORDERS:MANAGE');
  const canManage = canView;

  express.get('/productNotes', canView, express.crud.browseRoute.bind(null, app, ProductNote));
  express.post('/productNotes', canManage, express.crud.addRoute.bind(null, app, ProductNote));
  express.get('/productNotes/:id', canView, express.crud.readRoute.bind(null, app, ProductNote));
  express.put('/productNotes/:id', canManage, express.crud.editRoute.bind(null, app, ProductNote));
  express.delete('/productNotes/:id', canManage, express.crud.deleteRoute.bind(null, app, ProductNote));
};
