'use strict';

module.exports = (app, module) =>
{
  const {
    express,
    user,
    ProductTag
  } = module;

  const canView = user.auth('ORDERS:MANAGE');
  const canManage = canView;

  express.get('/productTags', canView, express.crud.browseRoute.bind(null, app, ProductTag));
  express.post('/productTags', canManage, express.crud.addRoute.bind(null, app, ProductTag));
  express.get('/productTags/:id', canView, express.crud.readRoute.bind(null, app, ProductTag));
  express.put('/productTags/:id', canManage, express.crud.editRoute.bind(null, app, ProductTag));
  express.delete('/productTags/:id', canManage, express.crud.deleteRoute.bind(null, app, ProductTag));
};
