'use strict';

exports.name = 'PaintShopIgnoredComponent';

exports.setUp = (app, mongoose) =>
{
  const ignoredComponentSchema = new mongoose.Schema({
    _id: String,
    mrps: [String],
    name: String
  }, {
    id: false,
    minimize: false
  });

  ignoredComponentSchema.statics.TOPIC_PREFIX = 'paintShop.ignoredComponents';
  ignoredComponentSchema.statics.BROWSE_LIMIT = 100;

  ignoredComponentSchema.index({mrps: 1});

  mongoose.model(exports.name, ignoredComponentSchema);
};
