'use strict';

exports.name = 'PaintShopLoadReason';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    active: {
      type: Boolean,
      required: true
    },
    position: {
      type: Number,
      required: true
    },
    label: {
      type: String,
      required: true,
      trim: true
    }
  }, {
    id: false,
    minimize: false
  });

  schema.statics.TOPIC_PREFIX = 'paintShop.loadReasons';
  schema.statics.BROWSE_LIMIT = 100;

  return schema;
};
