'use strict';

module.exports = (app, module, req, res, next) =>
{
  const {
    express,
    PaintShopLoad
  } = module;

  const options = {
    filename: 'WMES-PAINT_SHOP-LOAD',
    freezeRows: 1,
    freezeColumns: 1,
    columns: {
      time: {type: 'datetime', width: 18},
      duration: {type: 'decimal'}
    },
    serializeRow: doc =>
    {
      return {
        time: doc._id,
        duration: doc.d / 1000
      };
    },
    batchSize: 100,
    model: PaintShopLoad
  };

  express.crud.exportRoute(app, options, req, res, next);
};
