'use strict';

const step = require('h5.step');

module.exports = (app, module, req, res, next) =>
{
  const {
    PaintShopLoad
  } = module;

  const {secretKey, items} = req.body;
  const counter = req.body.counter > 0 ? +req.body.counter : 1;

  if (secretKey !== module.config.loadSecretKey)
  {
    return next(app.createError('INVALID_SECRET_KEY', 400));
  }

  if (!Array.isArray(items) || !items.length)
  {
    return next(app.createError('INVALID_ITEMS', 400));
  }

  const docs = items
    .map(item =>
    {
      return {
        _id: {
          ts: new Date(item._id),
          c: counter
        },
        d: item.d > module.config.maxLoadDuration ? 0 : item.d,
        r: null
      };
    })
    .filter(doc => !isNaN(doc._id.ts.getTime()) && doc.d >= 0);

  if (!docs.length)
  {
    return next(app.createError('INVALID_ITEMS', 400));
  }

  PaintShopLoad.updateLoad(counter, docs, err =>
  {
    if (err)
    {
      return next(err);
    }

    res.sendStatus(204);
  });
};
