'use strict';

exports.name = 'Ping';

exports.setUp = (app, mongoose) =>
{
  const pingSchema = new mongoose.Schema({
    time: Date,
    url: String,
    headers: {},
    user: String,
    ip: String
  }, {
    id: false
  });

  pingSchema.statics.TOPIC_PREFIX = 'pings';

  pingSchema.index({time: -1});
  pingSchema.index({'user.id': 1, time: -1});
  pingSchema.index({'user.ip': 1, time: -1});

  mongoose.model(exports.name, pingSchema);
};
