'use strict';

module.exports = (app, module) =>
{
  const {express, user, Ping} = module;

  express.options('/ping', setPingHeaders, (req, res) => res.end());

  express.get('/ping', setPingHeaders, (req, res) => res.send('pong'));

  express.get('/pings', user.auth('LOCAL', 'SUPER'), express.crud.browseRoute.bind(null, app, Ping));

  function setPingHeaders(req, res, next)
  {
    res.type('text/plain');
    res.set('Access-Control-Allow-Origin', '*');
    res.set('Access-Control-Allow-Methods', 'GET');
    res.set('Access-Control-Allow-Headers', req.headers['access-control-request-headers'] || '*');

    next();
  }
};
