'use strict';

exports.name = 'PlanOrderGroup';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    active: {
      type: Boolean,
      required: true
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    description: {
      type: String,
      trim: true
    },
    mrp: [String],
    productInclude: [[String]],
    productExclude: [[String]],
    bomInclude: [[String]],
    bomExclude: [[String]],
    names: {}
  }, {
    id: false,
    minimize: false
  });

  schema.statics.TOPIC_PREFIX = 'planning.orderGroups';
  schema.statics.BROWSE_LIMIT = 1000;

  schema.index({'$**': 'text'}, {name: 'text'});

  return schema;
};
