'use strict';

Object.assign(exports, require('util/createDictionaryModule')(
  require('./models/prodFunction'),
  'prodFunctions'
));

exports.modelPlugins = {
  User: (app, module, userSchema) =>
  {
    userSchema.path('prodFunction', {
      type: String,
      default: null,
      ref: 'ProdFunction'
    });

    userSchema.index({prodFunction: 1});

    userSchema.statics.dataAnonymizers.push(user =>
    {
      user.prodFunction = null;
    });
  }
};
