'use strict';

exports.name = 'ProdFunction';

exports.setUp = (app, mongoose) =>
{
  const prodFunctionSchema = new mongoose.Schema({
    _id: {
      type: String,
      required: true,
      trim: true
    },
    label: {
      type: String,
      trim: true
    },
    direct: {
      type: Boolean,
      default: false
    },
    dirIndirRatio: {
      type: Number,
      default: 100
    },
    color: {
      type: String,
      default: '#000000'
    }
  }, {
    id: false
  });

  prodFunctionSchema.statics.TOPIC_PREFIX = 'prodFunctions';
  prodFunctionSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, prodFunctionSchema);
};
