'use strict';

exports.name = 'ProdSerialNumber';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    _id: String,
    scannedAt: {
      type: Date,
      required: true
    },
    prodLine: {
      type: String,
      required: true
    },
    mrp: String,
    station: {
      type: Number,
      default: 0
    },
    prodShiftOrder: {
      type: String,
      default: null
    },
    orderNo: {
      type: String,
      required: true,
      pattern: /^[0-9]+$/
    },
    serialNo: {
      type: Number,
      required: true,
      min: 0,
      max: 9999
    },
    taktTime: {
      type: Number,
      required: true
    },
    sapTaktTime: {
      type: Number,
      required: true
    },
    iptTaktTime: {
      type: Number,
      default: 0
    },
    iptAt: {
      type: Date,
      default: null
    },
    bom: [String]
  }, {
    id: false,
    versionKey: false,
    minimize: false
  });

  schema.statics.TOPIC_PREFIX = 'prodSerialNumbers';

  schema.index({mrp: 1, scannedAt: -1});
  schema.index({orderNo: 1});
  schema.index({prodShiftOrder: 1, scannedAt: -1});
  schema.index({prodLine: 1, scannedAt: -1});
  schema.index({scannedAt: -1});
  schema.index({bom: 1}, {partialFilterExpression: {bom: {$type: 'array'}}});

  schema.post('save', function(doc)
  {
    if (app.production.recreating)
    {
      return;
    }

    app.broker.publish(`prodSerialNumbers.created.${doc.prodLine}`, doc.toJSON());
  });

  return schema;
};
