'use strict';

const unlockCommand = require('./unlockCommand');
const lockCommand = require('./lockCommand');
const syncCommand = require('./syncCommand');
const getPlannedQuantitiesCommand = require('./getPlannedQuantitiesCommand');
const joinCommand = require('./joinCommand');

module.exports = (app, module) =>
{
  const sio = app[module.config.sioId];

  sio.on('connection', function(socket)
  {
    socket.on('production.unlock', unlockCommand.bind(null, app, module, socket));
    socket.on('production.lock', lockCommand.bind(null, app, module, socket));
    socket.on('production.sync', syncCommand.bind(null, app, module, socket));
    socket.on('production.getPlannedQuantities', getPlannedQuantitiesCommand.bind(null, app, module, socket));
    socket.on('production.join', joinCommand.bind(null, app, module, socket));
    socket.on('production.leave', leaveProductionCommand.bind(null, socket));
  });

  function leaveProductionCommand(socket, prodLineId)
  {
    const prodLineState = module.getProdLineState(prodLineId);

    if (prodLineState)
    {
      prodLineState.onClientLeave(socket);
    }
  }
};
