'use strict';

const _ = require('lodash');

module.exports = (app, module, socket, logEntryStream, reply) =>
{
  const {user} = module;

  const creator = user.createUserInfo(socket.handshake.user, socket);

  module.syncLogEntryStream(creator, logEntryStream, (err, lockLogEntry) =>
  {
    if (err)
    {
      module.warn(`[sync] ${err.message}`);
    }

    if (lockLogEntry)
    {
      socket.emit('production.locked', {
        secretKey: lockLogEntry.secretKey,
        prodLine: lockLogEntry.prodLine
      });
    }

    if (_.isFunction(reply))
    {
      reply();
    }
  });
};
