'use strict';

module.exports = function(app, productionModule, prodLine, logEntry, done)
{
  productionModule.getProdData('shift', logEntry.prodShift, function(err, prodShift)
  {
    if (err)
    {
      productionModule.error(err, 'Failed to get a prod shift to change the quantities done.', {
        prodShiftId: logEntry.prodShift,
        prodLogEntryId: logEntry._id
      });

      return done(err);
    }

    if (!prodShift)
    {
      productionModule.warn(`Couldn't find prod shift to change the quantities done.`, {
        prodShiftId: logEntry.prodShift,
        prodLogEntryId: logEntry._id
      });

      return done();
    }

    prodShift.quantitiesDone[logEntry.data.hour].actual = Math.max(logEntry.data.newValue, 0);
    prodShift.markModified('quantitiesDone');

    prodShift.save(function(err)
    {
      if (err)
      {
        productionModule.error(err, 'Failed to save prod shift after changing the quantities done.', {
          prodShiftId: logEntry.prodShift,
          prodLogEntryId: logEntry._id
        });
      }

      return done(err);
    });
  });
};
