'use strict';

const step = require('h5.step');
const util = require('./util');

module.exports = function(app, productionModule, prodLine, logEntry, done)
{
  if (prodLine.isNew)
  {
    return done();
  }

  step(
    function updateProdLineStep()
    {
      prodLine.set({
        prodShiftOrder: null,
        prodDowntime: null
      });

      prodLine.save(this.next());
    },
    util.createRecalcShiftTimesStep(productionModule, logEntry),
    function finalizeStep(err)
    {
      if (err)
      {
        productionModule.error(err, 'Failed to save prod line after ending the work.', {
          prodLineId: prodLine._id,
          prodLogEntryId: logEntry._id
        });
      }

      return done(err);
    }
  );
};
